package BASIC_JAVA;

public class Comparison {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=6,b=6;
		if(a>b){
			System.out.println("a is greater than b");
		}
		else if(b>a){
			System.out.println("b is greater than a");
		}
		else {
			System.out.println("both are equal");
	}

	}
}
